head.script:
$j(document).ready(function() {
window.setTimeout(function() {
var href = document.location.href;
if (href.indexOf('?') == -1) {
if (href.indexOf('#') != -1) {
document.location.href = href.replace('#', '?_#');
} else {
document.location.href = href + '?_';
}
} else {
document.location.reload(true);
}
}, 15000);
})
